import React, { useEffect, useState } from 'react';

function App(){
  const [apod, setApod] = useState(null);
  const [date, setDate] = useState('');

  async function fetchApod(d){
    const url = '/api/apod' + (d ? `?date=${d}` : '');
    const res = await fetch(url);
    if(res.ok){
      const json = await res.json();
      setApod(json);
    }
  }

  useEffect(()=>{
    fetchApod();
  },[]);

  return (
    <div style={{padding:20,fontFamily:'Arial, sans-serif'}}>
      <h1>NASA APOD Explorer</h1>

      <div style={{marginBottom:12}}>
        <input type="date" value={date} onChange={e=>setDate(e.target.value)} />
        <button onClick={()=>fetchApod(date)} style={{marginLeft:8}}>Load</button>
      </div>

      {apod ? (
        <div>
          <h2>{apod.title} <small>({apod.date})</small></h2>
          {apod.media_type === 'image' ? (
            <img src={apod.url} alt={apod.title} style={{maxWidth:'100%',height:'auto'}} />
          ) : (
            <a href={apod.url} target="_blank" rel="noreferrer">Open media</a>
          )}
          <p>{apod.explanation}</p>
          {apod.copyright && <p>Â© {apod.copyright}</p>}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  )
}

export default App;
